mapper.select:
SELECT NVL(COUNT(*), 0)
FROM VOTER
WHERE POLLID=#{pollid} AND ID=#{id}

SELECT POLLID, ID, QUESTION, SDATE, EDATE, ITEMCOUNT, POLLTOTAL, REGDATE
FROM POLL
WHERE POLLID=#{pollid}

SELECT POLLSUBID, POLLID, ANSWER, ACOUNT
FROM POLLSUB
WHERE POLLID=#{pollid}

mapper.update:
UPDATE POLL
SET POLLTOTAL=POLLTOTAL + 1
WHERE POLLID = #{pollid}

UPDATE POLLSUB
SET ACOUNT = ACOUNT + 1
WHERE POLLSUBID=#{pollsubid} AND POLLID=#{pollid}

mapper.select:
SELECT A.POLLID as RID, A.QUESTION AS RQUESTION, B.ANSWER AS RANSWER, B.ACOUNT AS RACOUNT
FROM POLL A, POLLSUB B
WHERE A.POLLID=B.POLLID AND A.POLLID=#{pollid}
